action.body:
var item = listWidget.getSelectedItems()[0];
var windowParams = [];
windowParams['width'] = Math.floor(screen.width * 0.8);
windowParams['height'] = Math.floor(screen.height * 0.8);
wToolkit.dialog('task', 'ViewUserTask', { cmpref: item.id }, windowParams, true, false);

	if (confirm("&amp;modules.task.bo.actions.UnlockplannedtaskConfirm;"))
	{
		var item = listWidget.getSelectedItems()[0];
    	this.execute('task', 'UnlockPlannedTask', {cmpref: item.id}, this);
   		listWidget.refresh();
   	}

	var item = listWidget.getSelectedItems()[0];
    this.execute('task', 'UnlockPlannedTask', {cmpref: item.id, resetTime: 'true'}, this);
   	listWidget.refresh();
